const React = require("react");

class Clicker extends React.Component {
  constructor() {
    super();
    this.state = {
      points: 70,
      rate: 1,
      population: 100000,
      message: "",
      numberOfMines: 0
    };
  }

  handleClicker = () => {
    this.setState({
      points: this.state.points + this.state.rate
    });
  };

  buyOreMine = () => {
    if (this.state.points >= 100 + this.state.numberOfMines * 10) {
      this.setState({
        points: this.state.points - (100 + this.state.numberOfMines * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfMines += 1)
      });
    } else {
      this.setState({
        message: "Not enough Credit"
      });
    }
  };

  autoAccumulate = () => {
    if (this.state.rate === 1) {
      setInterval(() => {
        this.setState({
          points: (this.state.points += 1)
        });
      }, 1000);
    } else {
      setInterval(() => {
        this.setState({
          points: (this.state.points += 1) + this.state.rate
        });
      }, 1000);
    }
  };

  render() {
    const { points } = this.state;
    return (
      <div>
        <button onClick={this.autoAccumulate}>START</button>
        <button onClick={this.handleClicker}>Click Here</button>
        <button onClick={this.buyMiner}>'Buy a miner($100)'</button>
        <p>{this.state.message}</p>
        <p>Credit Per Second: {this.state.rate}</p>
        <p>Miners working: {this.state.numberOfMines}</p>
        <p>Galactic Credit Accumulated: {points}</p>
      </div>
    );
  }
}

export default Clicker;
