const React = require("react");

class Clicker extends React.Component {
  constructor() {
    super();
    this.state = {
      points: 1,
      rate: 0
    };
  }

  handleClicker = () => {
    this.setState({
      points: this.state.points + 1 + this.state.rate
    });
  };

  buyAddons = () => {
    this.setState({
      points: this.state.points - 100,
      rate: this.state.rate + 3
    });
  };

  autoAccumulate = () => {
    setInterval(() => {
      this.setState({
        points: 1
      });
    }, 1000);
  };

  render() {
    const { points } = this.state;
    return (
      <div>
        {this.autoAccumulate()}
        <button onClick={this.handleClicker}>Click Here</button>
        <button onClick={this.buyAddons}>
          'Click here to buy addon($100)'
        </button>
        <p>Galactic Credit Accumulated: {points}</p>
      </div>
    );
  }
}

export default Clicker;
