import React from "react";
import {countries} from "../src/countries.js"

class SignUpForm extends React.Component {
  constructor() {
    super();
    this.state = {
      name: "",
      email: "",
      DOB: ""
    }
  }

  handleChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    })
  }

  createCountriesOption = () => {
    for(let i = 0; i < countries.countries.length; i++) {
    return <select name="favorite_flavor"><option value={countries.countries[i].name}>{countries.countries[i].name}</option></select>
    }
  }

  render() {
    console.log(this.state)
    console.log(countries[0])
    return (
      <React.Fragment>
      <h1>Sign up for free trip to Mars FOREVER</h1>
      <div>
      <form onChange={this.handleChange}>
      <label htmlFor="name">Name: </label>
      <input type="text" placeholder="Your Name" name="name" value={this.state.name} id="name"/>
      <br />
      <label htmlFor="name">Email: </label>
      <input type="text" placeholder="Your Email" name="email" value={this.state.email} id="name"/>
      <input type="date" placeholder="Your DOB" name="DOB" value={this.state.DOB} id="name" />
      <this.createCountriesOption />
      </form>
      </div>
      </React.Fragment>
    )
  }
}

export default SignUpForm;
