const React = require("react");
const militiaImg = require("../src/Untitled-1.png");
const corpsecImg = require("../src/Untitled-2.png");
const colonialForceImg = require("../src/Untitled-3.png");
const fedPlanetFleetImg = require("../src/Untitled-4.png");
const fedOritalFleetImg = require("../src/Untitled-5.png");
const fedInterFleetImg = require("../src/Untitled-6.png");
const HMSJRJImg = require("../src/Untitled-7.png");
const smallDomeImg = require("../src/smallDome.png");
const domeGroupImg = require("../src/domeGroup.png");
const largeDomeImg = require("../src/largeDome.png");
const undergroundCityImg = require("../src/undergroundCity.png");
const undergroundNetworkImg = require("../src/undergroundNetwork.png");
const colonialCapitalImg = require("../src/colonialCapital.png");
const orbitalStructureImg = require("../src/orbitalStructure.png");
const oreMineImg = require("../src/oreMine.png");
const gasPlantImg = require("../src/gasPlant.png");
const agriDomeImg = require("../src/agriDome.png");
const waterPlantImg = require("../src/waterPlant.png");
const nanoFabricImg = require("../src/nanofabric.png");
const spacePortImg = require("../src/spacePort.png");
const orbitalStationImg = require("../src/orbitalStation.png");

const Sound = require("react-sound").default;
const themeSong = require("../src/theme.mp3");

class Clicker extends React.Component {
  constructor() {
    super();
    this.state = {
      points: 70,
      population: 100,
      populationGrowth: 0,
      rate: 1,
      message: "Start",
      militaryIndex: 0,
      numberOfMines: 0,
      numberOfGasProcessing: 0,
      numberOfAgricultureDome: 0,
      numberOfWaterPlant: 0,
      numberOfNanoFabricator: 0,
      numberOfSpacePort: 0,
      numberOfOrbitalStation: 0,
      numberOfSmallDome: 0,
      numberOfDomeGroup: 0,
      numberOfLargeDome: 0,
      numberOfUndergroundCity: 0,
      numberOfUndergroundNetwork: 0,
      numberOfColonialCapital: 0,
      numberOfOrbitalStructure: 0,
      numberOfMilitia: 0,
      numberOfCorpSecForce: 0,
      numberOfColonialForce: 0,
      numberOfFedPlanetFleet: 0,
      numberOfFedOrbitFleet: 0,
      numberOfFedInterFleet: 0,
      numberOfHMSJRJ: 0
    };
  }
  handleClicker = () => {
    this.setState({
      points: this.state.points + this.state.rate
    });
  };

  buyHMSJRJ = () => {
    let cost = 100;
    let populationCost = 100;
    if (
      this.state.points >= cost + this.state.numberOfHMSJRJ * 10 &&
      this.state.population >= populationCost + this.state.numberOfHMSJRJ * 10
    ) {
      this.setState({
        points: this.state.points - (cost + this.state.numberOfHMSJRJ * 10),
        militaryIndex: this.state.militaryIndex + 1,
        numberOfHMSJRJ: (this.state.numberOfHMSJRJ += 1),
        population: (this.state.population -= 100),
        message: "Unit Ready"
      });
    } else if (
      this.state.population <=
      populationCost + this.state.numberOfHMSJRJ * 10
    ) {
      this.setState({
        message: "Not enough People. Need " + (cost - this.state.population)
      });
    } else if (
      this.state.points <= cost + this.state.numberOfHMSJRJ * 10 &&
      cost - this.state.points > 0
    ) {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  buyFedInterFleet = () => {
    let cost = 100;
    let populationCost = 100;
    if (
      this.state.points >= cost + this.state.numberOfFedInterFleet * 10 &&
      this.state.population >=
        populationCost + this.state.numberOfFedInterFleet * 10
    ) {
      this.setState({
        points:
          this.state.points - (cost + this.state.numberOfFedInterFleet * 10),
        militaryIndex: this.state.militaryIndex + 1,
        numberOfFedInterFleet: (this.state.numberOfFedInterFleet += 1),
        population: (this.state.population -= 100),
        message: "Unit Ready"
      });
    } else if (
      this.state.population <=
      populationCost + this.state.numberOfFedInterFleet * 10
    ) {
      this.setState({
        message: "Not enough People. Need " + (cost - this.state.population)
      });
    } else if (
      this.state.points <= cost + this.state.numberOfFedInterFleet * 10 &&
      cost - this.state.points > 0
    ) {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  buyFedOrbitalFleet = () => {
    let cost = 100;
    let populationCost = 100;
    if (
      this.state.points >= cost + this.state.numberOfFedOrbitFleet * 10 &&
      this.state.population >=
        populationCost + this.state.numberOfFedOrbitFleet * 10
    ) {
      this.setState({
        points:
          this.state.points - (cost + this.state.numberOfFedOrbitFleet * 10),
        militaryIndex: this.state.militaryIndex + 1,
        numberOfFedOrbitFleet: (this.state.numberOfFedOrbitFleet += 1),
        population: (this.state.population -= 100),
        message: "Unit Ready"
      });
    } else if (
      this.state.population <=
      populationCost + this.state.numberOfFedOrbitFleet * 10
    ) {
      this.setState({
        message: "Not enough People. Need " + (cost - this.state.population)
      });
    } else if (
