const React = require("react");

class Clicker extends React.Component {
  constructor() {
    super();
    this.state = {
      points: 70,
      population: 100,
      populationGrowth: 0,
      rate: 1,
      message: "",
      numberOfMines: 0,
      numberOfGasProcessing: 0,
      numberOfAgricultureDome: 0,
      numberOfWaterPlant: 0,
      numberOfNanoFabricator: 0,
      numberOfSpacePort: 0,
      numberOfOrbitalStation: 0,
      numberOfSmallDome: 0
    };
  }

  handleClicker = () => {
    this.setState({
      points: this.state.points + this.state.rate
    });
  };

  buySmallDome = () => {
    let cost = 100000;
    if (this.state.points >= cost + this.state.numberOfOrbitalStation * 10) {
      this.setState({
        points:
          this.state.points - (cost + this.state.numberOfOrbitalStation * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfOrbitalStation += 1),
        population: (this.state.population += 100)
      });
    } else {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  buyOrbitalStation = () => {
    let cost = 100000;
    if (this.state.points >= cost + this.state.numberOfOrbitalStation * 10) {
      this.setState({
        points:
          this.state.points - (cost + this.state.numberOfOrbitalStation * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfOrbitalStation += 1),
        population: (this.state.population += 100)
      });
    } else {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  buySpacePort = () => {
    let cost = 10000;
    if (this.state.points >= cost + this.state.numberOfSpacePort * 10) {
      this.setState({
        points: this.state.points - (cost + this.state.numberOfSpacePort * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfSpacePort += 1),
        population: (this.state.population += 100)
      });
    } else {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  buyNanoFabricator = () => {
    let cost = 800;
    if (this.state.points >= cost + this.state.numberOfNanoFabricator * 10) {
      this.setState({
        points:
          this.state.points - (cost + this.state.numberOfNanoFabricator * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfNanoFabricator += 1),
        population: (this.state.population += 100)
      });
    } else {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  buyAgricultureDome = () => {
    let cost = 200;
    if (this.state.points >= cost + this.state.numberOfAgricultureDome * 10) {
      this.setState({
        points:
          this.state.points - (cost + this.state.numberOfAgricultureDome * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfAgricultureDome += 1),
        population: (this.state.population += 100)
      });
    } else {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  buyWaterPlant = () => {
    let cost = 200;
    if (this.state.points >= cost + this.state.numberOfWaterPlant * 10) {
      this.setState({
        points: this.state.points - (cost + this.state.numberOfWaterPlant * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfWaterPlant += 1),
        population: (this.state.population += cost)
      });
    } else {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  buyGasProcessing = () => {
    let cost = 200;
    if (this.state.points >= 100 + this.state.numberOfGasProcessing * 10) {
      this.setState({
        points:
          this.state.points - (100 + this.state.numberOfGasProcessing * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfGasProcessing += 1)
      });
    } else {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  buyOreMine = () => {
    let cost = 100;
    if (this.state.points >= 100 + this.state.numberOfMines * 10) {
      this.setState({
        points: this.state.points - (100 + this.state.numberOfMines * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfMines += 1)
      });
    } else {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  autoAccumulate = () => {
    setInterval(() => {
      this.setState({
        rate: this.state.rate,
        points: this.state.points + this.state.rate,
        populationGrowth: Math.floor(this.state.population * 0.01),
        population: this.state.population + this.state.populationGrowth
      });
    }, 1000);
  };

  render() {
    const { points } = this.state;
    return (
      <div>
        <h1>{"JRJ's Space CivilizationÂ®: Beyond Stars"}</h1>
        <div className="coreButtons">
          <button onClick={this.autoAccumulate}>START</button>
          <button onClick={this.handleClicker}>Click Here</button>
        </div>
        <div className="econBuildingsTabs">
          <button onClick={this.buyOreMine}>Ore Mine($100)</button>
          <button onClick={this.buyGasProcessing}>
            Gas Processing Facility($100)
          </button>
          <button onClick={this.buyAgricultureDome}>
            Agriculture Dome($200)
          </button>
          <button onClick={this.buyWaterPlant}>Water Processing Plant</button>
          <button onClick={this.buyNanoFabricator}>Nano Fabricator</button>
          <button onClick={this.buySpacePort}>Space Port</button>
          <button onClick={this.buyOrbitalStation}>Orbital Station</button>
        </div>
        <div className="coreStats">
          <p>Galactic Credit Accumulated ğŸ’°: ${points}</p>
          <p>Credit Per Second ğŸ’µ: {this.state.rate}</p>
          <p>Total Population ğŸ‘¨â€ğŸš€: {this.state.population}</p>
          <p>Population Growth Per Second ğŸ¤±: {this.state.populationGrowth}</p>
        </div>
        <p>{this.state.message}</p>
        <p>Ore Mines in Operation ğŸ—»: {this.state.numberOfMines}</p>
        <p>
          Gas Processing Facilities in Operation ğŸŒ‹:{" "}
          {this.state.numberOfGasProcessing}
        </p>
        <p>
          Agricultural Domes in Operation ğŸ¥¬:{" "}
          {this.state.numberOfAgricultureDome}
        </p>
        <p>
          Water Processing Plants in Operation ğŸ’§:{" "}
          {this.state.numberOfWaterPlant}
        </p>
        <p>
          Nano Fabricators in Operation âš™ï¸: {this.state.numberOfNanoFabricator}
        </p>
        <p>Space Ports in Operation ğŸ“¡: {this.state.numberOfSpacePort}</p>
        <p>
          Orbital Stations in Operation ğŸ›°: {this.state.numberOfOrbitalStation}
        </p>
      </div>
    );
  }
}

export default Clicker;
