const React = require("react");

class Clicker extends React.Component {
  constructor() {
    super();
    this.state = {
      points: 70,
      population: 100,
      populationGrowth: 0,
      rate: 1,
      message: "",
      numberOfMines: 0,
      numberOfGasProcessing: 0,
      numberOfAgricultureDome: 0,
      numberOfWaterPlant: 0,
      numberOfNanoFabricator: 0
    };
  }

  handleClicker = () => {
    this.setState({
      points: this.state.points + this.state.rate
    });
  };

  buyNanoFabricator = () => {
    let cost = 800
    if (this.state.points >= cost + this.state.numberOfNanoFabricator * 10) {
      this.setState({
        points:
          this.state.points - (cost + this.state.numberOfNanoFabricator * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfNanoFabricator += 1),
        population: (this.state.population += 100)
      });
    } else {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  buyAgricultureDome = () => {
    let cost = 200
    if (this.state.points >= cost + this.state.numberOfAgricultureDome * 10) {
      this.setState({
        points:
          this.state.points - (cost + this.state.numberOfAgricultureDome * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfAgricultureDome += 1),
        population: (this.state.population += 100)
      });
    } else {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  buyWaterPlant = () => {
    let cost = 200
    if (this.state.points >= cost + this.state.numberOfWaterPlant * 10) {
      this.setState({
        points:
          this.state.points - (cost + this.state.numberOfWaterPlant * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfWaterPlant += 1),
        population: (this.state.population += cost)
      });
    } else {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  buyGasProcessing = () => {
    let cost = 200
    if (this.state.points >= 100 + this.state.numberOfGasProcessing * 10) {
      this.setState({
        points:
          this.state.points - (100 + this.state.numberOfGasProcessing * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfGasProcessing += 1)
      });
    } else {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  buyOreMine = () => {
    let cost = 100
    if (this.state.points >= 100 + this.state.numberOfMines * 10) {
      this.setState({
        points: this.state.points - (100 + this.state.numberOfMines * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfMines += 1)
      });
    } else {
      this.setState({
        message: "Not enough Credit. Need " + "$" + (cost - this.state.points)
      });
    }
  };

  // setInitRate = () => {
  //   this.setState({
  //     rate: 1
  //   });
  // };

  autoAccumulate = () => {
    setInterval(() => {
      this.setState({
        rate: this.state.rate,
        points: this.state.points + this.state.rate,
        populationGrowth: Math.floor(this.state.population * 0.01),
        population: this.state.population + this.state.populationGrowth
      });
    }, 1000);
  };

  // populationIncrease = () => {
  //   setInterval(() => {
  //     this.setState({
  //       populationGrowth: Math.floor(this.state.population * 0.01)
  //     });
  //   }, 1000);
  // };

  render() {
    const { points } = this.state;
    return (
      <div>
      <h1>{"JRJ's Galactic Civilization: Stars and Beyond"}</h1>
        <button onClick={this.autoAccumulate}>
          START
        </button>
        <button onClick={this.handleClicker}>Click Here</button>
        <button onClick={this.buyOreMine}>'Buy an Ore Mine($100)'</button>
        <button onClick={this.buyGasProcessing}>
          'Buy a Gas Processing Facility($100)'
        </button>
        <button onClick={this.buyAgricultureDome}>
          'Buy an Agriculture Dome($200)'
        </button>
        <button onClick={this.buyWaterPlant}>'Buy a Water Processing Plant'</button>
        <button onClick={this.buyNanoFabricator}>'Buy a Nano Fabricator'</button>
        <p>{this.state.message}</p>
        <p>Credit Per Second ğŸ’µ: {this.state.rate}</p>
        <p>Population Growth Per Second ğŸ¤±: {this.state.populationGrowth}</p>
        <p>Ore Mines in Operation ğŸ—»: {this.state.numberOfMines}</p>
        <p>
          Gas Processing Facilities in Operation ğŸŒ‹:{" "}
          {this.state.numberOfGasProcessing}
        </p>
        <p>
          Agricultural Domes in Operation ğŸ¥¬: {this.state.numberOfAgricultureDome}
        </p>
        <p>Water Processing Plants in Operation ğŸ’§: {this.state.numberOfWaterPlant}</p>
        <p>Nano Fabricators in Operation âš™ï¸: {this.state.numberOfNanoFabricator}</p>
        <p>Galactic Credit Accumulated ğŸ’°: ${points}</p>
        <p>Total Population ğŸ‘¨â€ğŸš€: {this.state.population}</p>
      </div>
    );
  }
}

export default Clicker;
