const React = require("react");

class Clicker extends React.Component {
  constructor() {
    super();
    this.state = {
      points: 70,
      rate: 1,
      population: 100000,
      message: "",
      numberOfMines: 0,
      numberOfGasProcessing: 0,
      numberOfAgricultureDome: 0
    };
  }

  handleClicker = () => {
    this.setState({
      points: this.state.points + this.state.rate
    });
  };

  buyAgricultureDome = () => {
    if (this.state.points >= 100 + this.state.numberOfAgricultureDome * 10) {
      this.setState({
        points:
          this.state.points - (100 + this.state.numberOfAgricultureDome * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfAgricultureDome += 1)
      });
    } else {
      this.setState({
        message: "Not enough Credit"
      });
    }
  };

  buyGasProcessing = () => {
    if (this.state.points >= 100 + this.state.numberOfGasProcessing * 10) {
      this.setState({
        points:
          this.state.points - (100 + this.state.numberOfGasProcessing * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfGasProcessing += 1)
      });
    } else {
      this.setState({
        message: "Not enough Credit"
      });
    }
  };

  buyOreMine = () => {
    if (this.state.points >= 100 + this.state.numberOfMines * 10) {
      this.setState({
        points: this.state.points - (100 + this.state.numberOfMines * 10),
        rate: this.state.rate + 3,
        numberOfMines: (this.state.numberOfMines += 1)
      });
    } else {
      this.setState({
        message: "Not enough Credit"
      });
    }
  };

  setInitRate = () => {
    this.setState({
      rate: 1
    });
  };

  // autoAccumulate = () => {
  //   if (this.state.rate === 1) {
  //     setInterval(() => {
  //       this.setState({
  //         points: (this.state.points += 1)
  //       });
  //     }, 1000);
  //   } else {
  //     setInterval(() => {
  //       this.setState({
  //         points: (this.state.points += 1) + this.state.rate
  //       });
  //     }, 1000);
  //   }
  // };
  autoAccumulate = () => {
    setInterval(() => {
      this.setState({
        points: this.state.points + this.state.rate
      });
    }, 1000);
  };

  render() {
    const { points } = this.state;
    return (
      <div>
        <button onClick={(this.setInitRate, this.autoAccumulate)}>START</button>
        <button onClick={this.handleClicker}>Click Here</button>
        <button onClick={this.buyOreMine}>'Buy an Ore Mine($100)'</button>
        <button onClick={this.buyGasProcessing}>
          'Buy a Gas Processing Facility($100)'
        </button>
        <button onClick={this.buyAgricultureDome}>
          'Buy an Agriculture Dome($100)'
        </button>
        <p>{this.state.message}</p>
        <p>Credit Per Second: {this.state.rate}</p>
        <p>Ore Mines in Operation: {this.state.numberOfMines}</p>
        <p>
          Gas Processing Facilities in Operation:{" "}
          {this.state.numberOfGasProcessing}
        </p>
        <p>
          Agricultural Domes in Operation: {this.state.numberOfAgricultureDome}
        </p>
        <p>Galactic Credit Accumulated: {points}</p>
        <p>Total Populatin: {this.state.population}</p>
      </div>
    );
  }
}

export default Clicker;
